import React, { Fragment, useRef, useState, useEffect } from "react";
import { Grid, Button } from "@material-ui/core";
import NavbarItems from "./navbar/NavbarItems";

const Navbar = (props) => {
  const [sticky, setSticky] = useState();
  const navRef = useRef(null);
  useEffect(() => {
    window.onscroll = () => {
      console.log(window.pageYOffset);
      if (window.pageYOffset > navRef.current.offsetTop + 50) {
        setSticky("navbarFixedTop pb-2 pt-3");
      } else {
        setSticky("pb-4");
      }
    };
  }, [navRef]);

  return (
    <Fragment>
      <Grid
        container
        className={["navbarT", sticky].join(" ")}
        ref={(navbar) => {
          navRef.current = navbar;
        }}
      >
        <Grid item sm={1} className="v-center">
          <Button className="navBarBtn" variant="contained">
            ارسال درخواست
          </Button>
        </Grid>
        <Grid item sm={10} className="v-center">
          <nav className="navbar">
            <ul>
              <NavbarItems title={"خانه"} path={"/"} subMenu={false} />
              <NavbarItems title={"فروشگاه"} path={"/shop"} subMenu={true} />
              <NavbarItems
                title={"پشتیبانی"}
                path={"/support"}
                subMenu={false}
              />
              <NavbarItems title={"اخبار"} path={"/news"} subMenu={false} />
              <NavbarItems title={"بلاگ"} path={"/blog"} subMenu={false} />
              <NavbarItems
                title={"تماس با ما"}
                path={"/contact"}
                subMenu={false}
              />
              <NavbarItems
                title={"درباره ما"}
                path={"/about"}
                subMenu={false}
              />
            </ul>
          </nav>
        </Grid>
        <Grid item sm={1} className="v-center">
          <img src="/assets/img/logo.svg" alt="Logo" className="logo" />
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default Navbar;
